import api from './api';
import './App.css';

class App extends Component{

  /* ESTADO PARA SALVAR AS INFORMAÇÕES */
  state = {
    produtos: [],
  }

  /* BUSCA NA API */
  async componentDidMount(){
    const response = await api.get('${resultado}');

    this.setState({ produtos: response.data.results });
  }

  /* RENDERIZAR E RETORNAR OS VALORES */
  render(){

    const {produtos} = this.state;

    return(

      <div>

        {/* BARRA DE CIMA / BUSCA */}
        <header class='top-bar'>
          <div class="container">
            <div class="row">

              <div class="col-sm-4"><img src='./logo_ml.png' title='Logo Mercado Livre'></img></div>

              <div class="col-sm-8">

                <form>
                  <input type='text' placeholder='Nunca dejes de buscar'></input>
                  <button type='submit'><i class="bi bi-search"></i></button>

                  <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16">
                    <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
                  </svg>
                </form>

              </div>
            </div>            
          </div>
        </header>

        <div class='container'>
          {/* {produtos.map(results => (
            <li key={results.id}> 
              {results.title}
              <img src={results.thumbnail}></img>
            </li>
            ))
          } */}
        </div>
      </div>
    );
  }
}




export default App;
