import {Component} from 'react';
import api from './api';
import './App.css';

class App extends Component{

  /* ESTADO PARA SALVAR AS INFORMAÇÕES */
  state = {
    produtos: [],
  }

  /* BUSCA NA API */
  async componentDidMount(){
    const response = await api.get('{buscarForm}');

    this.setState({ produtos: response.data.results });
  }

  /* RENDERIZAR E RETORNAR OS VALORES */
  render(){

    const {produtos} = this.state;



    const form = document.getElementById('formulario');
    form.addEventListener('submit', function(e) {
      e.preventDefault();
      const [sobre, nome, titulo] = ['sobre', 'nome', 'titulo'].map(
        name => form.querySelector(`[name="${name}"]`).value
      );
      alert(`${sobre}, ${nome} - ${titulo}`);
    });



    return(
      <div>

        {/* BARRA DE CIMA / BUSCA */}

        <header class='top-bar'>
          <div class="container">
            <div class="row">

              <div class="col-sm-4"><img src='./logo_ml.png' title='Logo Mercado Livre'></img></div>

              <div class="col-sm-8">
                <form id='buscar-top-bar' method="GET">
                  <input type='text' id='buscarForm' placeholder='Nunca dejes de buscar'></input>
                  <button type='submit' value='enviar'>ok</button>
                </form>


                <form id="formulario" name="" method="" action="">
                  <p>
                    <h2 id="titulo-form">Dados do Livro</h2>
                  </p>
                  <p><label for="nome">Nome do Autor:</label>
                    <input type="text" name="nome" id="nome" maxlength="80" /></p>

                  <p><label for="sobre">Sobrenome do Autor:</label>
                    <input type="text" name="sobre" id="sobre" maxlength="80" /></p>

                  <p><label for="titulo">Título:</label>
                    <input type="text" name="titulo" id="titulo" maxlength="80" /></p>
                  <button type="submit">Enviar</button>
                </form>



              </div>
            </div>            
          </div>
        </header>

        {/* RETORNO DOS RESULTADOS */}
        <div class='container'>
          <div class='bg-white'>
            {produtos.map(results => (
              <li>                  
                <img src={results.thumbnail}></img>
                <div>
                  <span class='precio'>$ {results.price}</span>
                  <span>{results.title}</span>
                </div>
              </li>
              ))
            }
          </div>
        </div>
      </div>
    );
  }
}

export default App;
